class MSRc	def initialize				@opts = {}			end		def RcString(opts)				r = "rc "				r << %Q{/fo"#{opts['OUTFILE']}" "#{opts['INFILE']}"}				r		end		#	# Callbacks	#		def Opt(name, value)			@opts[name] = value		end		def Do			str = RcString(@opts)			puts str			out %x[#{str}]				($?.exitstatus > 0)?(false):(true)			endend